import React, { useState, useEffect } from 'react';
import '../App.css'; // Import the CSS file
import { Link } from 'react-router-dom';
import { vendiaClient } from '../vendiaClient';



const Home = () => {
  const [devices, setDevices] = useState([]);
  const [deviceName, setDeviceName] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const newDevice = {
      id: devices.length + 1,
      name: deviceName,
    };

    setDevices([...devices, newDevice]);
    setDeviceName('');
  };

  useEffect(() => {
    const fetchDevicesFromTable = async () => {
      try {
        const response = await client.entities.test.list();
        setDevices(response.items);
      } catch (error) {
        console.error('Error fetching data from table:', error);
      }
    };

    fetchDevicesFromTable();
  }, []);

  return (
    <div className="home-page">
      <h2>Current Devices</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={deviceName}
          onChange={(e) => setDeviceName(e.target.value)}
        />
        <button type="submit">Add Device</button>
      </form>
      <div className="device-container">
        {devices.map((device) => (
          <div key={device.id} className="device-item">
            <p>{device.name}</p>
            <button onClick={() => handleRemove(device.id)}>Remove</button><br></br>
            <button><Link to="/devicelist">View Tests</Link></button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
