import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';

const TableDesign = () => {
    const { client } = vendiaClient();
    const [testData, setTestData] = useState([]); // Initialize testData to an empty array
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchDataFromTable = async () => {
            try {
                const response = await client.entities.test.list();
                console.log('Response from table:', response);
                setTestData(response.items); // Assuming that the data is available in the 'items' array
                console.log('Test data:', testData); // Log the updated state to verify if it's being set correctly
            } catch (error) {
                console.error('Error fetching data from table:', error);
            }
        };

        fetchDataFromTable();
    }, []);

    const handleSearch = (e) => {
        const searchText = e.target.value.toLowerCase();
        const filteredResults = testData.filter((item) =>
            Object.values(item).some(
                (value) =>
                    value &&
                    typeof value === 'string' &&
                    value.toLowerCase().includes(searchText)
            )
        );
        setSearchTerm(searchText); // Update the searchTerm state here
        setTestData(filteredResults);
    };

    const handleDelete = async (index) => {
        const itemToDelete = testData[index];
        try {
            // Your delete function here, replace 'deleteData' with your actual function
            //await deleteData(itemToDelete);
            setTestData(testData.filter((_, i) => i !== index)); // Remove the item from testData
        } catch (error) {
            console.error('Error deleting data:', error);
        }
    };
    const handleEdit = (index) => {
        const itemToEdit = testData[index];
        // Implement the logic to open a modal with the specific data for editing
        console.log("Edit", itemToEdit);
    };

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-12 mb-3">
                    <h1 className="text-center">Device Table</h1>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-12 col-md-8">
                    <input
                        className="form-control mb-3"
                        type="text"
                        placeholder="Search by Name"
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th style={{ border: '1px solid black' }}>ID</th>
                                <th style={{ border: '1px solid black' }}>Org Assignment</th>
                                <th style={{ border: '1px solid black' }}>Name</th>
                                <th style={{ border: '1px solid black' }}>Method</th>
                                <th style={{ border: '1px solid black' }}>Notes</th>
                                <th style={{ border: '1px solid black' }}>Completed</th>
                                <th style={{ border: '1px solid black' }}>Updated By</th>
                                <th style={{ border: '1px solid black' }}>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {testData.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.TestID}</td>
                                    <td>{row.OrgAssignment}</td>
                                    <td>{row.TestName}</td>
                                    <td>{row.TestMethod}</td>
                                    <td>{row.Notes}</td>
                                    <td>
                                        <input
                                            type="checkbox"
                                            checked={row.Completed}
                                            onChange={(e) => {
                                                const updatedTestData = testData.map((item, i) => {
                                                    if (i === index) {
                                                        return { ...item, Completed: e.target.checked };
                                                    } else {
                                                        return item;
                                                    }
                                                });
                                                setTestData(updatedTestData);
                                            }}
                                        />
                                    </td>
                                    <td>{row.UpdatedBy}</td>
                                    <td>
                                        <button onClick={() => handleDelete(index)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

export default TableDesign;




