import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { vendiaClient } from '../vendiaClient';
import '../Home.css'; // Import the CSS file for additional styling

const Home = () => {
  const { entities } = vendiaClient();
  const [devices, setDevices] = useState([]);
  const [deviceName, setDeviceName] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Add a new device to Vendia entities
      const response = await entities.device.add({
        name: deviceName,
        completionPercentage: 0, // Assuming completion percentage starts at 0
      });

      // Update the state with the newly created device
      setDevices([...devices, response]);
      setDeviceName('');
    } catch (error) {
      console.error('Error adding device to Vendia:', error);
    }
  };

  useEffect(() => {
    const fetchDevicesFromTable = async () => {
      try {
        if (client && client.entities && client.entities.device) {
          // List devices from Vendia entities
          const response = await client.entities.device.list();
          setDevices(response.items);
          console.log(response.items);
        } else {
          console.error('Client, entities, or entities.device is undefined.');
        }
      } catch (error) {
        console.error('Error fetching data from table:', error);
      }
    };

    // Call the fetch function
    fetchDevicesFromTable();
  }, [client]);


  const handleRemove = async (deviceId) => {
    try {
      // Remove the device from Vendia entities
      await entities.device.remove(deviceId);

      // Update the state by removing the deleted device
      setDevices(devices.filter((device) => device.id !== deviceId));
    } catch (error) {
      console.error('Error removing device from Vendia:', error);
    }
  };

  return (
    <div className="home-page">
      <h2>Current Devices</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Device Name"
            aria-label="Device Name"
            aria-describedby="button-addon2"
            value={deviceName}
            onChange={(e) => setDeviceName(e.target.value)}
          />
          <button className="btn btn-outline-secondary" type="submit" id="button-addon2">
            Add Device
          </button>
        </div>
      </form>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {devices.map((device) => (
          <div key={device.id} className="col">
            <div className="card h-100 border-dark">
              <div className="card-body">
                <h5 className="card-title">{device.name}</h5>
                <p className="card-text">Completion Percentage: {device.completionPercentage}%</p>
                <button onClick={() => handleRemove(device.id)} className="btn btn-danger">
                  Remove
                </button>
                <br />
                <button className="btn btn-primary">
                  <Link to={`/devicelist/${device.id}`} className="text-white">
                    View Tests
                  </Link>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
