import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Modal, Button } from 'react-bootstrap';

const SummaryModal = ({ deviceId, onHide }) => {
    const { client } = vendiaClient();
    const [completedTests, setCompletedTests] = useState(0);
    const [incompleteTests, setIncompleteTests] = useState(0);

    useEffect(() => {
        const fetchTestsForDevice = async () => {
            try {
                // Fetch tests associated with the specific device
                const testsResponse = await client.entities.test.list();
                const devicesList = testsResponse.items;
                console.log('Tests Response:', testsResponse);

                let completedTestsCount = 0;
                let incompleteTestsCount = 0;

                await Promise.all(
                    devicesList.map(async (device) => {
                        try {
                            console.log('Processing Device:', device);

                            // Fetch tests associated with the device
                            const testsForDevice = testsResponse.items.filter((test) => test.Device === device.DeviceName);

                            // Process each test for the device
                            testsForDevice.forEach((test) => {
                                console.log('Processing Test:', test);

                                // Check if the test is completed or not
                                if (test.Completed) {
                                    completedTestsCount += 1;
                                } else {
                                    incompleteTestsCount += 1;
                                }
                            });
                        } catch (error) {
                            console.error('Error processing device:', error);
                        }
                    })
                );

                // Update state with the counts
                setCompletedTests(completedTestsCount);
                setIncompleteTests(incompleteTestsCount);
            } catch (error) {
                console.error('Error fetching tests response:', error);
            }
        };

        fetchTestsForDevice();
    }, [client, deviceId]);

    return (
        <Modal show={true} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>Summary</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <p>
                        <strong>Tests Completed:</strong> {completedTests}
                    </p>
                    <p>
                        <strong>Tests Incompleted:</strong> {incompleteTests}
                    </p>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default SummaryModal;
