import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Table, Button, Modal } from 'react-bootstrap';

const TableDesign = () => {
    const { client } = vendiaClient();
    const [testData, setTestData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [editRowIndex, setEditRowIndex] = useState(null);
    const [editedData, setEditedData] = useState({});
    const [showAddTestModal, setShowAddTestModal] = useState(false);
    const [newTest, setNewTest] = useState({
        TestID: '',
        OrgAssignment: '',
        TestName: '',
        TestMethod: '',
        Notes: '',
        Completed: false,
        UpdatedBy: '',
    });

    useEffect(() => {
        const fetchDataFromTable = async () => {
            try {
                const response = await client.entities.test.list();
                setTestData(response.items);
            } catch (error) {
                console.error('Error fetching data from table:', error);
            }
        };

        fetchDataFromTable();
    }, []);

    useEffect(() => {
        const updateDataInVendia = async () => {
            try {
                await client.entities.test.update(editedData);
            } catch (error) {
                console.error('Error updating data in Vendia:', error);
            }
        };

        updateDataInVendia();
    }, [testData]);

    const handleSearch = (e) => {
        const searchText = e.target.value.toLowerCase();
        const filteredResults = testData.filter((item) =>
            Object.values(item).some(
                (value) =>
                    value &&
                    typeof value === 'string' &&
                    value.toLowerCase().includes(searchText)
            )
        );
        setSearchTerm(searchText);
        setTestData(filteredResults);
    };

    const handleEdit = (index) => {
        setShowModal(true);
        setEditRowIndex(index);
        setEditedData(testData[index]);
    };

    const handleSave = async () => {
        const { _owner, _acl, ...updatedData } = editedData;
        const updatedTestData = [...testData];
        updatedTestData[editRowIndex] = updatedData;
        setTestData(updatedTestData);

        try {
            await client.entities.test.update(updatedData);
            const response = await client.entities.test.get(updatedData._id);
            const updatedTestDataAfterSave = updatedTestData.map((item) =>
                item._id === response._id ? response : item
            );
            setTestData(updatedTestDataAfterSave);
            setShowModal(false);
        } catch (error) {
            console.error('Error updating data in Vendia:', error);
        }
    };

    const handleDelete = async (index) => {
        const updatedTestData = [...testData];
        const deletedItem = updatedTestData.splice(index, 1)[0];
        setTestData(updatedTestData);

        try {
            await client.entities.test.remove(deletedItem._id);
        } catch (error) {
            console.error('Error deleting data in Vendia:', error);
        }
    };

    const handleAddNewTest = () => {
        setShowAddTestModal(true);
    };

    const handleSaveNewTest = async () => {
        try {
            await client.entities.test.add(newTest);
            const response = await client.entities.test.list();
            setTestData(response.items);
            setShowAddTestModal(false);
        } catch (error) {
            console.error('Error adding new test:', error);
        }
    };

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-12 mb-3">
                    <h1 className="text-center">Device Table</h1>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-12 col-md-8">
                    <input
                        className="form-control mb-3"
                        type="text"
                        placeholder="Search by Name"
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                    <Table responsive striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Org Assignment</th>
                                <th>Name</th>
                                <th>Method</th>
                                <th>Notes</th>
                                <th>Completed</th>
                                <th>Updated By</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {testData.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.TestID}</td>
                                    <td>{row.OrgAssignment}</td>
                                    <td>{row.TestName}</td>
                                    <td>{row.TestMethod}</td>
                                    <td>{row.Notes}</td>
                                    <td>{row.Completed ? 'Completed' : 'Not Completed'}</td>
                                    <td>{row.UpdatedBy}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(index)}>
                                            Edit
                                        </Button>
                                    </td>
                                    <td>
                                        <Button variant="danger" onClick={() => handleDelete(index)}>
                                            Delete
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Button variant="success" onClick={handleAddNewTest}>
                        Add New Test
                    </Button>
                </div>
            </div>
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                {/* ... (previous modal code) */}
            </Modal>

            <Modal show={showAddTestModal} onHide={() => setShowAddTestModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Test</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* Render input fields for the new test data */}
                    {/* Similar to the form in the previous modal */}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowAddTestModal(false)}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSaveNewTest}>
                        Save Test
                    </Button>
                </Modal.Footer>
            </Modal>

        </div>
    );
};

export default TableDesign;
