import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Modal, Button } from 'react-bootstrap';

const SummaryModal = ({ deviceId, onHide }) => {
    const { client } = vendiaClient();
    const [completedTests, setCompletedTests] = useState(0); // Initialize as numbers, not arrays
    const [incompleteTests, setIncompleteTests] = useState(0);

    useEffect(() => {
        const fetchTestsForDevice = async () => {
            try {
                // Fetch tests associated with the specific device
                const testsResponse = await client.entities.test.list();

                // Wait for the tests to be fetched
                console.log('Tests Response:', testsResponse);

                await Promise.all(
                    testsResponse.items.map(async (test) => {
                        try {
                            console.log('Processing Test:', test);

                            // Check if the test is associated with the specific device
                            if (test.Device === deviceId) {
                                if (test.Completed) {
                                    setCompletedTests((prevCompleted) => prevCompleted + 1);
                                } else {
                                    setIncompleteTests((prevIncomplete) => prevIncomplete + 1);
                                }
                            }
                        } catch (error) {
                            console.error('Error processing test:', error);
                        }
                    })
                );
            } catch (error) {
                console.error('Error fetching tests response:', error);
            }
        };


        fetchTestsForDevice();
    }, [client, deviceId]);

    return (
        <Modal show={true} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>Summary</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <p>
                        <strong>Tests Completed:</strong> {completedTests}
                    </p>
                    <p>
                        <strong>Tests Incompleted:</strong> {incompleteTests}
                    </p>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default SummaryModal;
