import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Table, Button, Modal } from 'react-bootstrap';

const TableDesign = () => {
    const { client } = vendiaClient();
    const [testData, setTestData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [editRowIndex, setEditRowIndex] = useState(null);
    const [editedData, setEditedData] = useState({});

    useEffect(() => {
        const fetchDataFromTable = async () => {
            try {
                const response = await client.entities.test.list();
                setTestData(response.items);
            } catch (error) {
                console.error('Error fetching data from table:', error);
            }
        };

        fetchDataFromTable();
    }, []);

    const handleSearch = (e) => {
        const searchText = e.target.value.toLowerCase();
        const filteredResults = testData.filter((item) =>
            Object.values(item).some(
                (value) =>
                    value &&
                    typeof value === 'string' &&
                    value.toLowerCase().includes(searchText)
            )
        );
        setSearchTerm(searchText);
        setTestData(filteredResults);
    };

    const handleEdit = (index) => {
        setShowModal(true);
        setEditRowIndex(index);
        setEditedData(testData[index]);
    };

    const handleSave = () => {
        const updatedTestData = [...testData];
        updatedTestData[editRowIndex] = editedData;
        setTestData(updatedTestData);
        setShowModal(false);

        // Implement logic to update the data in vendia using the updatedData.
        // You can use an API call or any other method according to your needs.
    };

    const handleDelete = (index) => {
        const updatedTestData = [...testData];
        updatedTestData.splice(index, 1);
        setTestData(updatedTestData);

        // Implement logic to delete the data in vendia. 
        // You can use an API call or any other method according to your needs.
    };

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-12 mb-3">
                    <h1 className="text-center">Device Table</h1>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-12 col-md-8">
                    <input
                        className="form-control mb-3"
                        type="text"
                        placeholder="Search by Name"
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                    <Table responsive striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Org Assignment</th>
                                <th>Name</th>
                                <th>Method</th>
                                <th>Notes</th>
                                <th>Completed</th>
                                <th>Updated By</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {testData.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.TestID}</td>
                                    <td>{row.OrgAssignment}</td>
                                    <td>{row.TestName}</td>
                                    <td>{row.TestMethod}</td>
                                    <td>{row.Notes}</td>
                                    <td>
                                        <input
                                            type="checkbox"
                                            checked={row.Completed}
                                            onChange={() => { }}
                                        />
                                    </td>
                                    <td>{row.UpdatedBy}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(index)}>
                                            Edit
                                        </Button>
                                    </td>
                                    <td>
                                        <Button variant="danger" onClick={() => handleDelete(index)}>
                                            Delete
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </div>
            </div>
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Data</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* Implement your form for editing data here */}
                    {/* Use the editedData state for initial values */}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSave}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default TableDesign;




