import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import Modal from './Modal'; // Replace with the appropriate path to your Modal component

const TableDesign = () => {
    const { client } = vendiaClient();
    const [testData, setTestData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [editModalOpen, setEditModalOpen] = useState(false);
    const [deleteModalOpen, setDeleteModalOpen] = useState(false);
    const [rowDataToEdit, setRowDataToEdit] = useState(null);
    const [rowDataToDelete, setRowDataToDelete] = useState(null);

    useEffect(() => {
        const fetchDataFromTable = async () => {
            try {
                const response = await client.entities.test.list();
                setTestData(response.items);
            } catch (error) {
                console.error('Error fetching data from table:', error);
            }
        };

        fetchDataFromTable();
    }, []);

    const handleSearch = (e) => {
        const searchText = e.target.value.toLowerCase();
        const filteredResults = testData.filter((item) =>
            Object.values(item).some(
                (value) =>
                    value &&
                    typeof value === 'string' &&
                    value.toLowerCase().includes(searchText)
            )
        );
        setSearchTerm(searchText);
        setTestData(filteredResults);
    };

    const handleEdit = (rowData) => {
        setRowDataToEdit(rowData);
        setEditModalOpen(true);
    };

    const handleSave = (updatedRowData) => {
        const updatedTestData = testData.map((item) =>
            item.TestID === updatedRowData.TestID ? updatedRowData : item
        );
        setTestData(updatedTestData);
        setEditModalOpen(false);
        // Update the data in vendia here
    };

    const handleDelete = (rowData) => {
        setRowDataToDelete(rowData);
        setDeleteModalOpen(true);
    };

    const handleDeleteConfirm = () => {
        const filteredData = testData.filter((item) => item.TestID !== rowDataToDelete.TestID);
        setTestData(filteredData);
        setDeleteModalOpen(false);
        // Update the data in vendia here
    };

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-12 mb-3">
                    <h1 className="text-center">Device Table</h1>
                </div>
            </div>
            <div className="row justify-content-center">
                <div className="col-12 col-md-8">
                    <input
                        className="form-control mb-3"
                        type="text"
                        placeholder="Search by Name"
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th style={{ border: '1px solid black' }}>ID</th>
                                <th style={{ border: '1px solid black' }}>Org Assignment</th>
                                <th style={{ border: '1px solid black' }}>Name</th>
                                <th style={{ border: '1px solid black' }}>Method</th>
                                <th style={{ border: '1px solid black' }}>Notes</th>
                                <th style={{ border: '1px solid black' }}>Completed</th>
                                <th style={{ border: '1px solid black' }}>Updated By</th>
                                <th style={{ border: '1px solid black' }}>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {testData.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.TestID}</td>
                                    <td>{row.OrgAssignment}</td>
                                    <td>{row.TestName}</td>
                                    <td>{row.TestMethod}</td>
                                    <td>{row.Notes}</td>
                                    <td>
                                        <input
                                            type="checkbox"
                                            checked={row.Completed}
                                            onChange={(e) => {
                                                const updatedTestData = testData.map((item, i) => {
                                                    if (i === index) {
                                                        return { ...item, Completed: e.target.checked };
                                                    } else {
                                                        return item;
                                                    }
                                                });
                                                setTestData(updatedTestData);
                                            }}
                                        />
                                    </td>
                                    <td>{row.UpdatedBy}</td>
                                    <td>
                                        <button onClick={() => handleEdit(row)}>Edit</button>
                                        <button onClick={() => handleDelete(row)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
            {editModalOpen && (
                <Modal
                    closeModal={() => setEditModalOpen(false)}
                    onSave={handleSave}
                    rowData={rowDataToEdit}
                />
            )}
            {deleteModalOpen && (
                <Modal
                    closeModal={() => setDeleteModalOpen(false)}
                    onDelete={handleDeleteConfirm}
                    rowData={rowDataToDelete}
                />
            )}
        </div>
    );
};

export default TableDesign;



