import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Modal, Button } from 'react-bootstrap';

const SummaryModal = ({ deviceId, onHide }) => {
    const { client } = vendiaClient();
    const [completedTests, setCompletedTests] = useState([]);
    const [incompleteTests, setIncompleteTests] = useState([]);
    useEffect(() => {
        const fetchTestsForDevice = async () => {
            try {
                // Fetch tests associated with the specific device
                const testsResponse = await client.entities.test.list();
                const devicesList = testsResponse.items;
                console.log("testsResponse", testsResponse);

                // Wait for the tests to be fetched
                const devicesWithCompletion = await Promise.all(
                    devicesList.map(async (device) => {
                        try {
                            // Fetch tests associated with the device
                            const testsResponse = await client.entities.test.list();
                            // loop through testsResponse to get the tests associated with the device
                            for (let i = 0; i < testsResponse.items.length; i++) {
                                if (testsResponse.items[i].Device === device.DeviceName) {
                                    console.log("device", device.DeviceName);
                                    console.log("test", testsResponse.items[i]);
                                }
                            }
                            // const deviceTests = testsResponse.items.filter((test) => test.Device === device.DeviceName);

                            // Separate completed and incomplete tests
                            const completedTests = deviceTests.filter((test) => test.Completed);
                            console.log("completed", completedTests.length);
                            const incompleteTests = deviceTests.filter((test) => !test.Completed);
                            console.log("incomplete", incompleteTests.length);

                            setCompletedTests(completedTests);
                            setIncompleteTests(incompleteTests);
                        } catch (error) {
                            console.error('Error fetching tests for device:', error);
                        }
                    })
                );
            } catch (error) {
                console.error('Error fetching tests response:', error);
            }
        };

        fetchTestsForDevice();
    }, [client, deviceId]);




    return (
        <Modal show={true} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>Summary</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <p>
                        <strong>Tests Completed:</strong> {completedTests.length}
                        {/* // change to completedTests.length after fixing useEffect */}
                    </p>
                    <p>
                        <strong>Tests Incompleted:</strong> {incompleteTests.length}

                    </p>
                    {/* // change to incompleteTests.length after fixing useEffect */}
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default SummaryModal;
