import React, { useState, useEffect } from 'react';
import { vendiaClient } from '../vendiaClient';
import { Modal, Button } from 'react-bootstrap';

const SummaryModal = ({ deviceId, onHide }) => {
    const { client } = vendiaClient();
    const [completedTests, setCompletedTests] = useState([]);
    const [incompleteTests, setIncompleteTests] = useState([]);
    const [devices, setDevices] = useState([]);
    const [deviceName, setDeviceName] = useState('');
    const [showSummaryModal, setShowSummaryModal] = useState(false);
    const [selectedDeviceId, setSelectedDeviceId] = useState(null);

    // useEffect(() => {
    //     const fetchDevicesFromTable = async () => {
    //       try {
    //         // Fetch the list of devices
    //         const devicesResponse = await client.entities.device.list();
    //         const devicesList = devicesResponse.items;

    //         // Calculate completion percentage for each device
    //         const devicesWithCompletion = await Promise.all(
    //           devicesList.map(async (device) => {
    //             // Fetch tests associated with the device
    //             const testsResponse = await client.entities.test.list();
    //             const deviceTests = testsResponse.items.filter((test) => test.Device === device.DeviceName);

    //             // Calculate completion percentage based on completed tests
    //             const completedTests = deviceTests.filter((test) => test.Completed);
    //             const completionPercentage = deviceTests.length > 0
    //               ? (completedTests.length / deviceTests.length) * 100
    //               : 100; // Set to 100% if no tests are assigned to the device

    //             return {
    //               ...device,
    //               Status: completionPercentage,
    //             };
    //           })
    //         );
    useEffect(() => {
        const fetchTestsForDevice = async () => {
            try {
                // Fetch tests associated with the device
                const testsResponse = await client.entities.test.list();
                const deviceTests = testsResponse.items.filter((test) => test.Device === deviceId);

                // Separate completed and incomplete tests
                const completed = deviceTests.filter((test) => test.Completed);
                const incomplete = deviceTests.filter((test) => !test.Completed);

                setCompletedTests(completed);
                setIncompleteTests(incomplete);
            } catch (error) {
                console.error('Error fetching tests for device:', error);
            }
        };

        fetchTestsForDevice();
    }, [client, deviceId]);

    return (
        <Modal show={true} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>Summary</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <p>
                        <strong>Tests Completed:</strong> {completedTests.length}
                    </p>
                    <p>
                        <strong>Tests Incompleted:</strong> {incompleteTests.length}
                    </p>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default SummaryModal;
